import { Injectable } from '@angular/core';

import { Item } from '../../models/item';

@Injectable()
export class Items {
  items: Item[] = [];

  defaultItem: any = {
    "name": "Burt Bear",
    "profilePic": "assets/img/speakers/bear.jpg",
    "about": "Burt is a Bear.",
  };


  constructor() {
    let items = [
      {
        "name": "Harry Potter and the Deathly Hallows",
        "profilePic": "https://www.carechartsuk.co.uk/wp-content/uploads/2014/10/book-reviews-web.png",
        "about": "Book, Fantasy, J.K. Rowling"
      },
      {
        "name": "Harry Potter and the Deathly Hallows",
        "profilePic": "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSxQZ7xz-WSPUMkhxhSteYeU_ncaq1bGQgy8MAJ1owYuT0cUNIQ9g",
        "about": "AudioBook, Fantasy, J.K. Rowling"
      },
      {
        "name": "Harry Potter and the Deathly Hallows",
        "profilePic": "data:image/jpeg;base64,",
        "about": "Movie, Fantasy, David Yates"
      },
      {
        "name": "To Kill a Mockingbird",
        "profilePic": "https://www.carechartsuk.co.uk/wp-content/uploads/2014/10/book-reviews-web.png",
        "about": "Book, Fiction, J.D. Salinger"
      },
      {
        "name": "Catcher in the Rye",
        "profilePic": "https://www.carechartsuk.co.uk/wp-content/uploads/2014/10/book-reviews-web.png",
        "about": "Book, Fiction, J.D. Salinger"
      },
      {
        "name": "The Shining",
        "profilePic": "data:image/jpeg;base64,",
        "about": "Movie, Horror, Stanley Kubrick"
      },
      {
        "name": "The Notebook",
        "profilePic": "data:image/jpeg;base64,",
        "about": "Movie, Romance, Nick Cassavetes"
      }
    ];

    for (let item of items) {
      this.items.push(new Item(item));
    }
  }

  query(params?: any) {
    if (!params) {
      return this.items;
    }

    return this.items.filter((item) => {
      for (let key in params) {
        let field = item[key];
        if (typeof field == 'string' && field.toLowerCase().indexOf(params[key].toLowerCase()) >= 0) {
          return item;
        } else if (field == params[key]) {
          return item;
        }
      }
      return null;
    });
  }

  add(item: Item) {
    this.items.push(item);
  }

  delete(item: Item) {
    this.items.splice(this.items.indexOf(item), 1);
  }
}
